ExpenseDate=(props)=>{
    const month = props.date.toLocaleString("tr-Tr", { month: "long" });
    const day = props.date.toLocaleString("tr-Tr", { day: "2-digit" });
    const year = props.date.getFullYear();
    return (
        <div>
        <div>{month}</div>
        <div>{day}</div>
        <div>{year}</div>
      </div>
    );

}
export default ExpenseDate;
